export const login = (credentials) => {
const loginUrl = `/api/user/login?username=${credentials.username}&password=${credentials.password}`;

// fetch will return something called a Promise
return fetch(loginUrl, {
method: "POST",
headers: {
"Content-Type": "application/json",
},
}).then((response) => {
if (response.status < 200 || response.status >= 300) {
throw Error("Fail to log in");
}
}); // returns a promise
};

export const signup = (data) => {
const signupUrl = "/api/user/register";

return fetch(signupUrl, {
method: "POST",
headers: {
"Content-Type": "application/json",
},
body: JSON.stringify(data), // convert a JavaScript object (data) into a JSON string representation.
}).then((response) => {
if (response.status < 200 || response.status >= 300) {
throw Error("Fail to sign up");
}
});
};
