      rating: 4.7,
      visitTime: "1 hour",
      description: "Iconic clock tower of the Palace of Westminster",
      category: "Historical",
    },
    {
      id: 10,
      name: "Tower Bridge",
      type: "bridge",
      icon: "ðŸŒ‰",
      coordinates: { lat: 51.5055, lng: -0.0754 },
      rating: 4.6,
      visitTime: "1-2 hours",
      description: "Famous bascule and suspension bridge",
      category: "Historical",
    },
  ],
};

// Google Maps ç»„ä»¶
const GoogleMapComponent = ({
  center,
  zoom,
  attractions,
  onAttractionClick,
  selectedAttraction,
}) => {
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const markersRef = useRef([]);

  const onLoad = useCallback((mapInstance) => {
    mapRef.current = mapInstance;
    setMap(mapInstance);
  }, []);

  useEffect(() => {
    if (map && attractions) {
      // æ¸…é™¤æ—§çš„æ ‡è®°
      markersRef.current.forEach((marker) => marker.setMap(null));
      markersRef.current = [];

      // åˆ›å»ºæ–°çš„æ ‡è®°
      const newMarkers = attractions.map((attraction) => {
        const marker = new window.google.maps.Marker({
          position: attraction.coordinates,
          map: map,
          title: attraction.name,
          icon: {
            url: `data:image/svg+xml;charset=UTF-8,${encodeURIComponent(`
              <svg width="40" height="40" viewBox="0 0 40 40" xmlns="http://www.w3.org/2000/svg">
                <circle cx="20" cy="20" r="18" fill="white" stroke="#3B82F6" stroke-width="2"/>
                <text x="20" y="28" text-anchor="middle" font-size="16">${attraction.icon}</text>
              </svg>
            `)}`,
            scaledSize: new window.google.maps.Size(40, 40),
          },
        });

        marker.addListener("click", () => {
          onAttractionClick(attraction);
        });

        return marker;
      });

      markersRef.current = newMarkers;
    }
  }, [map, attractions, onAttractionClick]);

  useEffect(() => {
    if (map && center) {
      map.setCenter(center);
    }
  }, [center, map]);

  return (
    <div
      ref={mapRef}
      style={{ width: "100%", height: "100%" }}
      onLoad={onLoad}
    />
  );
};

// åœ°å›¾æ¸²æŸ“ç»„ä»¶
const MapWithWrapper = ({
  currentCity,
  onAttractionClick,
  selectedAttraction,
}) => {
  const center = cityCoordinates[currentCity] || cityCoordinates["Paris"];
  const attractions = attractionsData[currentCity] || attractionsData["Paris"];

  const render = (status) => {
    switch (status) {
      case Status.LOADING:
        return (
          <div className="flex items-center justify-center h-full bg-gray-100">
            <div className="text-center">
              <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
              <p className="text-gray-600">Loading Google Maps...</p>
              <p className="text-sm text-gray-500 mt-2">City: {currentCity}</p>
            </div>
          </div>
        );
      case Status.FAILURE:
        return (
          <div className="flex items-center justify-center h-full bg-red-50">
            <div className="text-center text-red-600 p-6">
              <h3 className="text-lg font-bold mb-2">
                Error loading Google Maps
              </h3>
              <p className="text-sm mb-4">
                Please check your API key and internet connection
              </p>
              <div className="text-xs text-gray-600 bg-gray-100 p-3 rounded">
                <p>API Key: {GOOGLE_MAPS_API_KEY ? "Set" : "Missing"}</p>
                <p>City: {currentCity}</p>
                <p>Coordinates: {JSON.stringify(center)}</p>
              </div>
              <button
                onClick={() => window.location.reload()}
                className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
              >
                Retry
              </button>
            </div>
          </div>
        );
      default:
        return (
          <GoogleMapComponent
            center={center}
            zoom={13}
            attractions={attractions}
            onAttractionClick={onAttractionClick}
            selectedAttraction={selectedAttraction}
          />
        );
    }
  };

  // å¦‚æžœæ²¡æœ‰ API Keyï¼Œæ˜¾ç¤ºæç¤º
  if (!GOOGLE_MAPS_API_KEY || GOOGLE_MAPS_API_KEY === "YOUR_API_KEY_HERE") {
    return (
      <div className="flex items-center justify-center h-full bg-yellow-50">
        <div className="text-center p-6">
          <h3 className="text-lg font-bold text-yellow-800 mb-2">
            Google Maps API Key Required
          </h3>
          <p className="text-sm text-yellow-700 mb-4">
